            // auto submission
            return keccak256(
                abi.encodePacked(
                    packedSubmission,
                    autoParams.executionFee,
                    autoParams.flags,
                    autoParams.fallbackAddress,
                    autoParams.data,
                    autoParams.nativeSender
                )
            );
        }
