// SPDX-License-Identifier: MIT
pragma solidity 0.8.7;

interface IConfirmationAggregator {
    /* ========== STRUCTS ========== */

    struct SubmissionInfo {
        uint8 confirmations; // received confirmations count
        uint8 requiredConfirmations; // required oracles (DSRM) received confirmations count
        bool isConfirmed; // is confirmed submission (user can claim)
        mapping(address => bool) hasVerified; // verifier => has already voted
    }
    struct DebridgeDeployInfo {
        uint256 chainId; //native chainId
        bytes nativeAddress; //native token address
        uint8 confirmations; // received confirmations count
        uint8 requiredConfirmations; // required oracles (DSRM) received confirmations count
        uint8 decimals;
        string name;
        string symbol;
        mapping(address => bool) hasVerified; // verifier => has already voted
    }

    /* ========== EVENTS ========== */

    event DeployConfirmed(bytes32 deployId, address operator); // emitted once the submission is confirmed by one oracle
    event Confirmed(bytes32 submissionId, address operator); // emitted once the submission is confirmed by one oracle

    /* ========== FUNCTIONS ========== */

    function submit(bytes32 _submissionId) external;

    function submitMany(bytes32[] memory _submissionIds) external;

    function getSubmissionConfirmations(bytes32 _submissionId)
        external
        view
        returns (uint8 _confirmations, bool _isConfirmed);

    function getConfirmedDeployId(bytes32 _debridgeId)
        external
        view
        returns (bytes32);
}

contracts/periphery/CallProxy.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.7;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/ICallProxy.sol";
import "../libraries/Flags.sol";

contract CallProxy is Initializable, AccessControlUpgradeable, ICallProxy {
    using SafeERC20 for IERC20;
    using Flags for uint256;

    /* ========== STATE VARIABLES ========== */

    bytes32 public constant DEBRIDGE_GATE_ROLE = keccak256("DEBRIDGE_GATE_ROLE"); // role allowed to withdraw fee
    uint256 public override submissionChainIdFrom;
    bytes public override submissionNativeSender;

    /* ========== ERRORS ========== */

    error DeBridgeGateBadRole();

    error ExternalCallFailed();
    error CallFailed();

    /* ========== MODIFIERS ========== */

    modifier onlyGateRole() {
        if (!hasRole(DEBRIDGE_GATE_ROLE, msg.sender)) revert DeBridgeGateBadRole();
        _;
    }

    /* ========== CONSTRUCTOR  ========== */

    function initialize() public initializer {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    function call(
        address _reserveAddress,
        address _receiver,
        bytes memory _data,
        uint256 _flags,
        bytes memory _nativeSender,
        uint256 _chainIdFrom
    ) external payable override onlyGateRole returns (bool _result) {
        uint256 amount = address(this).balance;

        _result = externalCall(
            _receiver,
            amount,
            _data,
            _nativeSender,
            _chainIdFrom,
            _flags.getFlag(Flags.PROXY_WITH_SENDER)
        );

        if (!_result && _flags.getFlag(Flags.REVERT_IF_EXTERNAL_FAIL)) {
            revert ExternalCallFailed();
        }
        if (!_result) {
            (bool success, ) = _reserveAddress.call{value: amount}(new bytes(0));
            if (!success) revert CallFailed();
        }
    }

    function callERC20(
        address _token,
        address _reserveAddress,
        address _receiver,
        bytes memory _data,
        uint256 _flags,
        bytes memory _nativeSender,
        uint256 _chainIdFrom
    ) external override onlyGateRole returns (bool _result) {
        uint256 amount = IERC20(_token).balanceOf(address(this));
        IERC20(_token).safeApprove(_receiver, 0);
        IERC20(_token).safeApprove(_receiver, amount);

        _result = externalCall(
            _receiver,
            0,
            _data,
            _nativeSender,
            _chainIdFrom,
            _flags.getFlag(Flags.PROXY_WITH_SENDER)
        );

        amount = IERC20(_token).balanceOf(address(this));

        if (!_result &&_flags.getFlag(Flags.REVERT_IF_EXTERNAL_FAIL)) {
            revert ExternalCallFailed();
        }
        if (!_result || amount > 0) {
            IERC20(_token).safeTransfer(_reserveAddress, amount);
        }
    }

    //gnosis
    //https://github.com/gnosis/MultiSigWallet/blob/ca981359cf5acd6a1f9db18e44777e45027df5e0/contracts/MultiSigWallet.sol#L244-L261

    function externalCall(
        address destination,
        uint256 value,
        bytes memory data,
        bytes memory _nativeSender,
        uint256 _chainIdFrom,
        bool storeSender
    ) internal returns (bool result) {
        // Temporary write to a storage nativeSender and chainIdFrom variables.
        // External contract can read them during a call if needed
        if (storeSender) {
            submissionChainIdFrom = _chainIdFrom;
            submissionNativeSender = _nativeSender;
        }
        uint256 dataLength = data.length;
        assembly {
            let x := mload(0x40) // "Allocate" memory for output (0x40 is where "free memory" pointer is stored by convention)
            let d := add(data, 32) // First 32 bytes are the padded length of data, so exclude that
            result := call(
                sub(gas(), 34710), // 34710 is the value that solidity is currently emitting
                // It includes callGas (700) + callVeryLow (3, to pay for SUB) + callValueTransferGas (9000) +
                // callNewAccountGas (25000, in case the destination address does not exist and needs creating)
                destination,
                value,
                d,
                dataLength, // Size of the input (in bytes) - this is what fixes the padding problem
                x,
                0 // Output is ignored, therefore the output size is zero
            )
        }

        // clear storage variables to get gas refund
        if (storeSender) {
            submissionChainIdFrom = 0;
            submissionNativeSender = "";
        }
    }

    // we need to accept ETH from deBridgeGate
    receive() external payable {
    }

    // ============ Version Control ============
    function version() external pure returns (uint256) {
        return 120; // 1.0.3
    }
}

contracts/mock/MockProxyReceiver.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/ICallProxy.sol";

// MockProxyReceiver receives payable and non-payable calls from CallProxy.sol.
// Used for positive proxy tests
contract MockProxyReceiver {
    using SafeERC20 for IERC20;

    uint256 public result = 0;
    uint256[] public resultArray;
    uint256 public weiReceived = 0;
    uint256 public tokensReceived;
    string public lastHit = "";
    uint256 public submissionChainIdFrom;
    bytes public submissionNativeSender;

    function setUint256Payable(uint256 _result) external payable {
        lastHit = "setUint256Payable";
        result = _result;
        weiReceived = msg.value;
    }

    function setArrayUint256Payable(uint256[] memory _result) external payable {
        lastHit = "setArrayUint256Payable";
        resultArray = _result;
        weiReceived = msg.value;
    }

    function setArrayAndPullToken(
        address _token,
        uint256 _amount,
        uint256[] memory _result
    ) external {
        lastHit = "setArrayAndPullToken";
        resultArray = _result;
        uint256 balanceBefore = IERC20(_token).balanceOf(address(this));
        IERC20(_token).safeTransferFrom(address(msg.sender), address(this), _amount);
        uint256 balanceAfter = IERC20(_token).balanceOf(address(this));
        tokensReceived = balanceAfter - balanceBefore;
    }

    function setUint256AndPullToken(
        address _token,
        uint256 _amount,
        uint256 _result
    ) external {
        lastHit = "setUint256AndPullToken";
        result = _result;
        uint256 balanceBefore = IERC20(_token).balanceOf(address(this));
        IERC20(_token).safeTransferFrom(address(msg.sender), address(this), _amount);
        uint256 balanceAfter = IERC20(_token).balanceOf(address(this));
        tokensReceived = balanceAfter - balanceBefore;
    }

    function pullTokenAndSetNativeSender(
        address _token,
        uint256 _amount,
        uint256 _result
    ) external {
        lastHit = "pullTokenAndSetNativeSender";
        result = _result;
        uint256 balanceBefore = IERC20(_token).balanceOf(address(this));
        IERC20(_token).safeTransferFrom(address(msg.sender), address(this), _amount);
        uint256 balanceAfter = IERC20(_token).balanceOf(address(this));
        tokensReceived = balanceAfter - balanceBefore;
        submissionChainIdFrom = ICallProxy(msg.sender).submissionChainIdFrom();
        submissionNativeSender = ICallProxy(msg.sender).submissionNativeSender();
    }


    // This function is called for all messages sent to
    // this contract, except plain Ether transfers
    // (there is no other function except the receive function).
    // Any call with non-empty calldata to this contract will execute
    // the fallback function (even if Ether is sent along with the call).
    fallback() external payable {
        lastHit = "fallback";
        weiReceived = msg.value;
    }

    // This function is called for plain Ether transfers, i.e.
    // for every call with empty calldata.
    receive() external payable {
        lastHit = "receive";
        weiReceived = msg.value;
    }
}

contracts/mock/MockProxyConsumer.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "../interfaces/ICallProxy.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract MockProxyConsumer {
    using SafeERC20 for IERC20;

    uint256 public constant CHAIN_ID_FROM = 42;

    address public callProxy;
    address public token;
    bool public lastOperationStatus;

    constructor(address _callProxy, address _token) {
        callProxy = _callProxy;
        token = _token;
    }

    function transferToken(
        address _token,
        address _receiver,
        address _fallbackAddress,
        bytes memory _data
    ) external payable {
        bool status;
        if (_token == address(0)) {
            status = ICallProxy(callProxy).call{value: msg.value}(
                _fallbackAddress,
                _receiver,
                _data,
                0,
                "",
                CHAIN_ID_FROM
            );
        } else {
            IERC20(_token).transfer(callProxy, msg.value);
            status = ICallProxy(callProxy).callERC20(
                _token,
                _fallbackAddress,
                _receiver,
                _data,
                0,
                "",
                CHAIN_ID_FROM
            );
        }
        lastOperationStatus = status;
    }
}

